vlocity_ins-omniscript-custom-lwc.slds-p-right_small.slds-m-bottom_xx-small.slds-show_inline-block.slds-size_12-of-12.slds-medium-size_12-of-12 {
    grid-column: span 2;
    padding: 0;
    margin: 0;
}

.cCenterPanel.slds-m-top--x-large.slds-p-horizontal--medium,
[data-omni-key="personalData"].slds-grid.slds-wrap.slds-p-vertical_small.omniscript-step__body,
slot.slds-grid.slds-wrap.slds-size_1-of-1.slds-p-left_medium,
vlocity_ins-omniscript-select.slds-p-right_small.slds-m-bottom_xx-small.slds-show_inline-block.slds-size_12-of-12.slds-medium-size_6-of-12.omni-read-only,
vlocity_ins-omniscript-text.slds-p-right_small.slds-m-bottom_xx-small.slds-show_inline-block.slds-size_12-of-12.slds-medium-size_6-of-12.omni-read-only,
vlocity_ins-omniscript-typeahead.omni-typeahead.omni-lookup.slds-p-right_small.slds-m-bottom_xx-small.slds-show_inline-block.slds-size_12-of-12, 
c-zurich_select-custom.slds-p-right_small.slds-m-bottom_xx-small.slds-show_inline-block.slds-size_12-of-12.slds-medium-size_6-of-12,
vlocity_ins-omniscript-typeahead-block.slds-m-bottom_xx-small.slds-show_inline-block.slds-size_12-of-12.slds-medium-size_6-of-12,
.slds-grid.slds-wrap.slds-p-vertical_small.omniscript-step__body,
.slds-col.slds-p-around_x-small.slds-size_12-of-12{
    padding: 0;
    width: 100%;
    margin-bottom: 0;
}

.vlc-separator.slds-border_top.slds-m-top_x-small.slds-m-bottom_xx-small.slds-m-horizontal_medium {
    display: none;
}

vlocity_ins-omniscript-step {
    width: 70%;
    margin: auto;
}

[data-omni-key="Block4"],
[data-omni-key="Block5"] {
    display: none;
}

[data-omni-key="Block6"] {
    width: 100%;
    padding: 0;
    margin: 0;
}

[data-omni-key="Block6"] > ul > li > section.slds-accordion__section.slds-p-horizontal_none.slds-is-open,
[data-omni-key="Block6"] > ul > li > section.slds-accordion__section.slds-p-horizontal_none.slds-is-open > .slds-accordion__summary {
    padding: 0;
}

.omni-block_content[vlocity_ins-omniscriptBlock_omniscriptBlock] {padding: 0}

.slds-input[readonly] {padding: 0 1rem;}

@media (min-width: 48em) {
    .omniscript-body[data-stepborder='right'][c-zurichErrorServiceEnglish_zurichErrorServiceEnglish] {
    border-right: 0px !important;
    }
}

